INSERT INTO `article` VALUES (1, 8, '商家入驻流程', '&lt;p&gt;如果您需要入驻本商城。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;需要先在商城里注册一个会员账号，如图：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/03-02/f9a30a44769e79b7687abd55be2c8f9d.png&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;1088&quot; height=&quot;695&quot;/&gt;&lt;/p&gt;&lt;p&gt;注册账号成功以后，在申请入驻页里填写入驻信息&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/03-02/6d30dbdb5d12f3838f37e3ace78e3be5.png&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;1094&quot; height=&quot;780&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/03-02/892fd093645b56225d021968ec0f493c.png&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;1092&quot; height=&quot;598&quot;/&gt;入驻申请提交以后，等平台审核即可，若审核成功代表店铺入驻成功，可在商家中心登录账号管理店铺&lt;/p&gt;', '', '', '', 2, 1, 1519972944, '', 0, '', '', 1253, 1520006400, ''), (2, 23, '商城入驻功能重新上线', '&lt;p&gt;商城入驻功能重新上线，广大店家可入驻&lt;/p&gt;', '', '', '', 2, 1, 1519973384, '', 0, '', '', 1171, 1520006400, ''), (3, 12, '退款售后流程', '&lt;p&gt;以下是商品售后流程：&lt;/p&gt;&lt;p&gt;在商城还未发货之前，您可以直接取消订单，商城会根据您的付款方式退还您的金额。&lt;/p&gt;&lt;p&gt;如果商品已发货，或者在您收到货以后对商品不满意，您可以针对单个商品发起售后&lt;/p&gt;', '', '', '', 2, 1, 1519973595, '', 0, '', '', 1243, 1520006400, ''), (4, 3, '购物流程', '&lt;p&gt;您需要先在商城注册一个会员号，才能进行下单。&lt;/p&gt;&lt;p&gt;注册完成以后，可在本商城挑选商品&lt;/p&gt;&lt;p&gt;挑选好中意的商品以后，结算即可。&lt;/p&gt;&lt;p&gt;商城会根据您的收货地址为您发货。&lt;/p&gt;', '', '', '', 2, 1, 1519973853, '', 0, '', '', 1122, 1520006400, ''), (5, 4, '商城支持的支付方式', '&lt;p&gt;商城暂时支持微信/支付宝/，余额/积分抵扣，信用卡/银行卡支付&lt;/p&gt;', '', '', '', 2, 1, 1519973972, '', 0, '', '', 1073, 1520006400, ''), (6, 23, '商城入驻功能上线', '&lt;p&gt;商城入驻功能上线商城入驻功能上线商城入驻功能上线商城入驻功能上线商城入驻功能上线商城入驻功能上线&lt;/p&gt;', '', '', '', 2, 1, 1519974146, '', 0, '', '', 1036, 1520006400, ''), (8, 20, '促销活动', '&lt;p&gt;666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666666&lt;/